(function () {
    cc.BauCuaHistoryListView = cc.Class({
        "extends": cc.ListView,
        properties: {
            sprDice1: cc.Sprite,
            sprDice2: cc.Sprite,
            sprDice3: cc.Sprite
        },

        initialize: function (messages) {
            this.messages = messages;
            var countMessage = this.messages.length;

            let controller = cc.BauCuaController.getInstance();
            for (var i = 0; i < countMessage; ++i) {
                if (i == 0) {
                    this.sprDice1.spriteFrame = controller.getSfDiceHistory(parseInt(this.messages[i].Dice1) - 1);
                    this.sprDice2.spriteFrame = controller.getSfDiceHistory(parseInt(this.messages[i].Dice2) - 1);
                    this.sprDice3.spriteFrame = controller.getSfDiceHistory(parseInt(this.messages[i].Dice3) - 1);
                }  else {
                    var item = cc.instantiate(this.itemTemplate);
                    this.content.addChild(item);
                    item.getComponent(cc.BauCuaHistoryItem).updateItem(this.messages[i], i);
                    this.items.push(item);
                }
            }
        }
    });
}).call(this);
