(function () {
    cc.SicBoGroupUserView = cc.Class({
        extends: cc.Component,
        properties: {
            itemTemplate:cc.Node,
            content:cc.Node,
            animation:cc.Animation
        },
        onLoad:function()
        {
            this.getGroupUser();
        },
        getGroupUser: function () {
            var getGroupUserCommand = new cc.SicBoGroupUserCommand;
            getGroupUserCommand.execute(this);
        },
        onGetGroupUserResponse: function (response) {
            var list = response;
            //var list = slotsHistoryListData;
            if (list !== null && list.length > 0) {
                this.resetList();
                this.initialize(list);
            }
        },
        resetList:function()
        {
            for (let index = 0; index < this.content.children.length; index++) {
                this.content.children[index].destroy();
            }
        },
        initialize:function(list)
        {
            for (let index = 0; index < list.length; index++) {
                var item = cc.instantiate(this.itemTemplate);
                this.content.addChild(item);
                var itemInfo = item.getComponent(cc.SicBoGroupItem);
                itemInfo.updateItem(list[index]);
            }
            this.animation.play('swipe').wrapMode = cc.WrapMode.Normal;
        },
        closeClicked: function () {
            this.resetList();
            this.animation.play('swipe').wrapMode = cc.WrapMode.Reverse;
            var self = this;
            var delay = 0.4;
            cc.AudioController.getInstance().playSound(cc.AudioTypes.SICBO_CLOSE_POPUP);
            cc.director.getScheduler().schedule(function () {
                self.animation.stop();
                cc.SicBoPopupController.getInstance().destroyGroupUserView();
            }, this, 1, 0, delay, false);
        },
    })
}).call(this)