(function () {
    cc.SicBoHistoryItem = cc.Class({
        "extends": cc.Component,
        properties: {
            // sprite: cc.Sprite,
            lbBet: cc.Label,
            lbWin: cc.Label,
            spriteDiceBet: cc.Sprite,
            winColor:cc.Color,
            loseColor:cc.Color,
            bgSprite:cc.Node
        },

        updateItem: function (item,isActiveBg) {
            // this.sprite.enabled = itemID % 2 !== 0;
            let controller = cc.SicBoController.getInstance();
            let listTuImage = controller.getTuImage();
            this.spriteDiceBet.spriteFrame = listTuImage[parseInt(item.GateID) - 1];
            this.lbBet.string = cc.Tool.getInstance().formatNumberSicbo(item.Bet);
            if (item.Award==0) {
                this.lbWin.node.color = this.loseColor;
                this.lbWin.string = "-" + cc.Tool.getInstance().formatNumberSicbo(item.Bet);
            }else
            {
                this.lbWin.node.color = this.winColor;
                this.lbWin.string = "+" + cc.Tool.getInstance().formatNumberSicbo(item.Award);

            }
            if (isActiveBg) {
                this.bgSprite.active = true;
            }
        }
    });
}).call(this);