(function () {
    cc.SicBoSettingView = cc.Class({
        "extends": cc.Component,
        properties: {
            animation: cc.Animation,
            nodeOffset: cc.Node,
            blackBG: cc.Node
        },

        onLoad: function () {
            this.openPopup = false;
        },

        start: function () {
        },

        openSettingClicked: function () {
            this.animation.play('openSettings').wrapMode = cc.WrapMode.Normal;
            cc.AudioController.getInstance().playSound(cc.AudioTypes.SICBO_OPEN_POPUP);
            this.blackBG.active = true;
        },

        closeSettingClicked: function () {
            this.animation.play('openSettings').wrapMode = cc.WrapMode.Reverse;
            let that = this;
            setTimeout(() => {
                that.blackBG.active = false;
            }, 500);
        }
        ,
        closeSoundEffect:function()
        {
            cc.AudioController.getInstance().playSound(cc.AudioTypes.SICBO_CLOSE_POPUP);
        }

    });
}).call(this)

